mapper.select:
    SELECT COUNT(*)
	  FROM (SELECT SS.sdt as sdt, SS.edt as edt
	          FROM busk B, busk_req BR, busk_stag BS, stag_sche SS 
	         WHERE B.bno = BR.bno
	           AND BR.brno = BS.brno
	           AND BS.ssno = SS.ssno
	           AND B.bno=#{no}
	         GROUP BY BS.brno
	       ) c
	 WHERE sdt BETWEEN #{sdt} and #{edt}
	    OR edt BETWEEN #{sdt} and #{edt}

SELECT count(sdt)
  FROM per_sche
 WHERE sdt BETWEEN #{sdt} and #{edt}
    OR edt BETWEEN #{sdt} and #{edt}
   AND bno=#{no}

mapper.update:
update per_sche
   set sdt=#{nsdt}, edt=#{nedt}, addr=#{addr}, x=#{x}, y=#{y}, shopname=#{shopname}, cnt=#{cnt}, cdt=now()
 where psno=#{sno}

mapper.select:
select ssno as sno
from busk_stag
where brno = #{no}

SELECT COUNT(*) 
  FROM 
        (SELECT sdt, edt
           FROM (SELECT SS.sdt as sdt, SS.edt as edt
                   FROM busk B, busk_req BR, busk_stag BS, stag_sche SS 
                  WHERE B.bno = BR.bno
                    AND BR.brno = BS.brno
                    AND BS.ssno = SS.ssno
                    AND B.bno=#{no}
               GROUP BY BS.brno
                 ) c
          WHERE sdt NOT BETWEEN #{sdt} and #{edt}
            AND edt NOT BETWEEN #{sdt} and #{edt}
         ) d
 WHERE sdt BETWEEN #{nsdt} and #{nedt}
    OR edt BETWEEN #{nsdt} and #{nedt}

 SELECT COUNT(*)
 FROM(SELECT sdt, edt
        FROM per_sche
       WHERE sdt NOT BETWEEN #{sdt} and #{edt}
         AND edt NOT BETWEEN #{sdt} and #{edt}
         AND bno=#{no}
     )  c
WHERE sdt BETWEEN #{nsdt} and #{nedt}
   OR edt BETWEEN #{nsdt} and #{nedt}
