mapper.select:
select S.sno, S.name, SP.phot, SUBSTR(SC.sdt,1,10), S.bas_addr
  from sup S, stag_sche_190108 SC, stag_phot SP
 where S.sno = SC.sno 
   and S.sno = SP.sno
   and SUBSTR(SC.sdt,1,10) between #{startDate} and #{endDate}
  GROUP BY S.sno;

select S.sno, S.name, SP.phot, SUBSTR(SC.sdt,1,10), S.bas_addr
  from sup S, stag_sche_190108 SC, stag_phot SP
 where S.name like #{keyword}
   and S.sno = SC.sno 
   and S.sno = SP.sno
   and SUBSTR(SC.sdt,1,10) between #{startDate} and #{endDate}
  GROUP BY S.sno;

select S.sno, S.name, SP.phot, SUBSTR(SC.sdt,1,10), S.bas_addr
  from sup S, stag_sche_190108 SC, stag_phot SP
 where S.bas_addr like #{keyword}
   and S.sno = SC.sno 
   and S.sno = SP.sno
   and SUBSTR(SC.sdt,1,10) between #{startDate} and #{endDate}
  GROUP BY S.sno;

select S.sno, S.name, SP.phot, SUBSTR(SC.sdt,1,10), S.bas_addr
  from sup S, stag_sche_190108 SC, stag_phot SP
 where S.genre like #{keyword}
   and S.sno = SC.sno 
   and S.sno = SP.sno
   and SUBSTR(SC.sdt,1,10) between #{startDate} and #{endDate}
  GROUP BY S.sno;
